            intersection_exists = False
            for r in range(h_start_row, min(h_end_row, grid.shape[0])):
                for c in range(v_start_col, min(v_end_col, grid.shape[1])):
                    if 0 <= r < grid.shape[0] and 0 <= c < grid.shape[1]:
                        intersection_exists = True
